void SDVStoppingDistCheck(){ //function that determines the stopping dist of the SDV based on speed
    if (SDV_speed == 20)
        SDV_StopDist = 6;
    else if (SDV_speed == 30)
        SDV_StopDist = 13;
    else if (SDV_speed == 40)
        SDV_StopDist = 23;
    else if (SDV_speed == 50)
        SDV_StopDist = 36;
    else if (SDV_speed == 60)
        SDV_StopDist = 52;
    else if (SDV_speed == 70)
        SDV_StopDist = 71;
}

void subcontroller(){
    if (B_Injuries==T_Injuries && B_Injuries < S_Injuries) 
    {
        if (B_SDV_Damage > T_SDV_Damage)
            Action = 2;
        else if (B_SDV_Damage < T_SDV_Damage)
            Action = 1;
        else if (B_SDV_Damage == T_SDV_Damage)
            Action = 4;
    }
    else if (B_Injuries==S_Injuries && B_Injuries < T_Injuries) 
    {
        if (B_SDV_Damage > S_SDV_Damage)
            Action = 3;
        else if (B_SDV_Damage < S_SDV_Damage)
            Action = 1;
        else if (B_SDV_Damage == S_SDV_Damage)
            Action = 1;
    }
    else if (S_Injuries==T_Injuries && S_Injuries < B_Injuries) 
    {
        if (T_SDV_Damage > S_SDV_Damage)
            Action = 3;
        else if (T_SDV_Damage < S_SDV_Damage)
            Action = 2;
        else if (T_SDV_Damage == S_SDV_Damage)
            Action = 2;
    }
    else if (S_Injuries==T_Injuries && T_Injuries==B_Injuries) 
    {
        if (S_SDV_Damage < T_SDV_Damage && S_SDV_Damage < B_SDV_Damage)
            Action = 3;
        else if (B_SDV_Damage < T_SDV_Damage)
            Action = 1;
        else if (T_SDV_Damage < B_SDV_Damage)
            Action = 2;
        else
            Action = 4;
    }
}

void resetValues(){
    B_Injuries = 0;
    T_Injuries = 0;
    S_Injuries = 0;
    Injuries = 0;
    B_SDV_Damage = 0;
    T_SDV_Damage = 0;
    S_SDV_Damage = 0;
    SDV_Damage = 0;
    Action = 4;
    TurnDone = false; 
    SharpDone = false;
    BrakeDone = false;
}

